    @Override
    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
        for (BizTable tb : DBConfig.DB_Tables) {
            db.execSQL(tb.GetDropTableQry());
        }
        onCreate(db);
    }

