    public long AddRecord(BizTable tb) {
        long RV = 0;
        SQLiteDatabase db = this.getWritableDatabase();

        ContentValues values = new ContentValues();
        Log.d("bala", "AddRecord Start");
        for (int i = 1; i < tb.Fields.length; i++) {
            values.put(tb.Fields[i].FName, tb.Fields[i].FValue);
            Log.d("bala", "Name = " + tb.Fields[i].FName + ", Value = " + tb.Fields[i].FValue);
        }
        RV = db.insert(tb.TableName, null, values);
        Log.d("bala", "RV = " + RV);
        db.close();
        Log.d("bala", "AddRecord End");
        return RV;
    }

    public long UpdateRecord(BizTable tb) {
        long RV = 0;
        SQLiteDatabase db = this.getWritableDatabase();
        ContentValues values = new ContentValues();
        Log.d("bala", "UpdateRecord Start");
        for (int i = 1; i < tb.Fields.length; i++) {
            values.put(tb.Fields[i].FName, tb.Fields[i].FValue);
        }
        RV = db.update(tb.TableName, values, tb.Fields[0].FName + " = ?",
                new String[]{tb.Fields[0].FValue});
        db.close();
        Log.d("bala", "RV = " + RV);
        Log.d("bala", "UpdateRecord end");
        return RV;
    }


    public long DeleteRecord(BizTable tb, String Id) {
        long RV = 0;
        Log.d("bala", "DeleteRecord start");
        SQLiteDatabase db = this.getWritableDatabase();
        RV = db.delete(tb.TableName, tb.Fields[0].FName + " = ?",
                new String[]{Id});
        db.close();
        Log.d("bala", "DeleteRecord end");
        return RV;
    }

