    public String GetRecordJSON(BizTable tb, String id) {
        SQLiteDatabase db = this.getReadableDatabase();

        Cursor cursor = db.query(tb.TableName, tb.GetFieldNames(), tb.Fields[0].FName + "=?", new String[]{id}, null, null, null, null);
        if (cursor != null) cursor.moveToFirst();
        JSONObject obj = new JSONObject();

        Log.d("bala", "GetRecordJSON Start");
        String FName, FValue;
        for (int i = 0; i < tb.Fields.length; i++) {
            try {
                FName = tb.Fields[i].FName;
                FValue = cursor.getString(i);
                Log.d("bala", "FName = " + FName);
                Log.d("bala", "FValue = " + FValue);
                obj.put(FName, FValue);
            } catch (Exception ex) {
            }
        }
        Log.d("bala", "GetRecordJSON End");

        return obj.toString();
    }

