    public static boolean exportDB(Context context, String dbName) {
        String DATABASE_NAME = dbName;
        String databasePath = context.getDatabasePath(DATABASE_NAME).getPath();
        String inFileName = databasePath;
        try {
            File dbFile = new File(inFileName);
            FileInputStream fis = new FileInputStream(dbFile);

            File outFileName = new File(Environment.getExternalStorageDirectory().getPath() + "/DenRestaurant/"+ "/DataBase/");
            if(!outFileName.exists())outFileName.mkdirs();

            outFileName = new File(outFileName,DATABASE_NAME);
            if(outFileName.exists())outFileName.delete();
            OutputStream output = new FileOutputStream(outFileName);

            byte[] buffer = new byte[1024];
            int length;
            while ((length = fis.read(buffer)) > 0) {
                output.write(buffer, 0, length);
            }
            //Close the streams
            output.flush();
            output.close();
            fis.close();
            msg("Export Successful");
            return true;
        } catch (Exception e) {
            return false;
        }
    }

    public static boolean importDB(Context context, String dbName) {
        String DATABASE_NAME = dbName;
        File db_path = new File(Environment.getExternalStorageDirectory().getPath() + "/DenRestaurant/"+ "DataBase/");
        db_path = new File(db_path,DATABASE_NAME);
        String inFileName = db_path.getAbsolutePath();

        try {

            File dbFile = new File(inFileName);
            FileInputStream fis = new FileInputStream(dbFile);

            FileOutputStream fos = new FileOutputStream(context.getDatabasePath(DATABASE_NAME).getPath());

            byte[] buffer = new byte[1024];
            int length;
            while ((length = fis.read(buffer)) > 0) {
                fos.write(buffer, 0, length);
            }

            fos.flush();
            fos.close();
            fis.close();

            msg("Import Successful");
            return true;

        } catch (Exception e) {
            return false;
        }
    }

